import React from 'react'
import { useState } from 'react'
import iconArrow from '../images/icon-arrow-up.svg'
import iconArrowDown from '../images/icon-arrow-down.svg'
import openIcon from '../images/icon-menu.svg'
import closeIcon from '../images/icon-close-menu.svg'
import todo from '../images/icon-todo.svg'
import calender from '../images/icon-calendar.svg'
import reminder from '../images/icon-reminders.svg'
import planning from '../images/icon-planning.svg'

const Navbar = () => {
  const [open, setOpen] = useState(false)
  const [open2, setOpen2] = useState(false)
  const [navbarOpen, setNavbarOpen] = useState(false)

  const handleDropDownOpen = () => {
    setOpen(true)
  }
  const handleDropDownClose = () => {
    setOpen(false)
  }
  const handleDropDownOpen2 = () => {
    setOpen2(true)
  }
  const handleDropDownClose2 = () => {
    setOpen2(false)
  }

  const toggleMenu = () => {
    setNavbarOpen(!navbarOpen)
  }
  const hideMenu = () => {
    setNavbarOpen(false)
  }
  return (
    <section className='layout'>
      <div className='header_nav-logo'>snap</div>
      <div className='nav-list' id={navbarOpen ? 'hide-item' : 'show-item'}>
        {' '}
        <div className='d-flex'>
          <div className='dropdown' onClick={hideMenu}>
            <div
              className='dropdown-menu'
              onMouseEnter={handleDropDownOpen}
              onMouseLeave={handleDropDownClose}
            >
              <div className='icon-dropdown'>
                {' '}
                Features
                {open ? (
                  <img src={iconArrow} alt='' />
                ) : (
                  <img src={iconArrowDown} alt='' />
                )}
              </div>
              {open ? (
                <div className='dropdownlist' onMouseEnter={handleDropDownOpen}>
                  <div>
                    <img src={todo} alt='' /> Todo List
                  </div>
                  <div>
                    <img src={calender} alt='' /> Calender
                  </div>
                  <div>
                    <img src={reminder} alt='' /> Reminders
                  </div>
                  <div>
                    <img src={planning} alt='' /> Planning
                  </div>
                </div>
              ) : null}
            </div>
          </div>
          <div className='dropdown' onClick={hideMenu}>
            <div
              className='dropdown-menu'
              onMouseEnter={handleDropDownOpen2}
              onMouseLeave={handleDropDownClose2}
            >
              <div className='icon-dropdown'>
                {' '}
                Company
                {open2 ? (
                  <img src={iconArrow} alt='' />
                ) : (
                  <img src={iconArrowDown} alt='' />
                )}
              </div>
              {open2 ? (
                <ul className='dropdownlist' onMouseEnter={handleDropDownOpen2}>
                  <li>History</li>
                  <li>Our Team</li>
                  <li>Blog</li>
                </ul>
              ) : null}
            </div>
          </div>
          <li onClick={hideMenu}>Careers</li>
          <li onClick={hideMenu}>About</li>
        </div>
      </div>
      <div className='marginLeft'>
        {' '}
        <div className='right'>
          <button className='btn1'>Login</button>
          <button className='reg'>Register</button>
        </div>
      </div>
      <div className='menu-icon'>
        {navbarOpen ? (
          <img src={closeIcon} alt='' />
        ) : (
          <img src={openIcon} alt='' />
        )}
      </div>
    </section>
  )
}

export default Navbar
