import React from 'react'
import { useState } from 'react'
import iconArrow from '../images/icon-arrow-up.svg'
import iconArrowDown from '../images/icon-arrow-down.svg'
import openIcon from '../images/icon-menu.svg'
import closeIcon from '../images/icon-close-menu.svg'
const Hero = () => {
  const [open, setOpen] = useState(false)
  const [open2, setOpen2] = useState(false)
  const [navbarOpen, setNavbarOpen] = useState(false)

  const handleDropDownOpen = () => {
    setOpen(true)
  }
  const handleDropDownClose = () => {
    setOpen(false)
  }
  const handleDropDownOpen2 = () => {
    setOpen2(true)
  }
  const handleDropDownClose2 = () => {
    setOpen2(false)
  }

  const toggleMenu = () => {
    setNavbarOpen(true)
  }
  const hideMenu = () => {
    setNavbarOpen(false)
  }
  return (
    <header className='header'>
      <div className='header_nav-logo'>snap</div>
      <nav
        className='header_nav-left'
        // id={navbarOpen ? 'show-item' : 'hide-item'}
      >
        <ul className='nav-list'>
          <li className='dropdown'>
            <div
              className='dropdown-menu'
              onMouseEnter={handleDropDownOpen}
              onMouseLeave={handleDropDownClose}
            >
              <li className='icon-dropdown'>
                {' '}
                Features
                {open ? (
                  <img src={iconArrow} alt='' />
                ) : (
                  <img src={iconArrowDown} alt='' />
                )}
              </li>
              {open ? (
                <ul>
                  <li>Todo List</li>
                  <li>Calender</li>
                  <li>Reminders</li>
                  <li>Planning</li>
                </ul>
              ) : null}
            </div>
          </li>
          <li className='dropdown'>
            <div
              className='dropdown-menu'
              onMouseEnter={handleDropDownOpen2}
              onMouseLeave={handleDropDownClose2}
            >
              <li className='icon-dropdown'>
                {' '}
                Features
                {open2 ? (
                  <img src={iconArrow} alt='' />
                ) : (
                  <img src={iconArrowDown} alt='' />
                )}
              </li>
              {open2 ? (
                <ul onMouseEnter={handleDropDownOpen2}>
                  <li>Todo List</li>
                  <li>Calender</li>
                  <li>Reminders</li>
                  <li>Planning</li>
                </ul>
              ) : null}
            </div>
          </li>
          <li>Careers</li>
          <li>About</li>
        </ul>
        {/* <ul className='header_nav-left--list'>
          <li onMouseEnter={handleClose} onMouseLeave={handleOpen}>
            <li className='icon-dropdown'>
              {' '}
              Features
              {open ? (
                <img src={iconArrow} alt='' />
              ) : (
                <img src={iconArrowDown} alt='' />
              )}
            </li>
            <li>
              <ul className='menu'>
                <li className='menu-item'>
                  <button>Todo List</button>
                </li>
                <li className='menu-item'>
                  <button>Calender</button>
                </li>
                <li className='menu-item'>
                  <button>Reminders</button>
                </li>
                <li className='menu-item'>
                  <button>Planning</button>
                </li>
              </ul>
            </li>
          </li>
          <li className='icon-dropdown' onClick={handleOpen2}>
            {' '}
            Features
            {open ? (
              <img src={iconArrow} alt='' />
            ) : (
              <img src={iconArrowDown} alt='' />
            )}
          </li>
          <li>
            <ul className='menu' style={{ display: open2 ? '' : 'none' }}>
              <li className='menu-item'>
                <button>Todo List</button>
              </li>
              <li className='menu-item'>
                <button>Calender</button>
              </li>
              <li className='menu-item'>
                <button>Reminders</button>
              </li>
              <li className='menu-item'>
                <button>Planning</button>
              </li>
            </ul>
          </li>
          <li>Careers</li>
          <li>About</li>
        </ul>
        <ul className='right'>
          <li className='header_nav-right--list--item right'>Login</li>
          <li
            className='header_nav-right--list--item header_nav-right--list--item2 '
            onClick={hideMenu}
          >
            Register
          </li>
        </ul> */}
      </nav>
      <div className='menu-icon' onClick={toggleMenu}>
        {navbarOpen ? (
          <img src={closeIcon} alt='' />
        ) : (
          <img src={openIcon} alt='' />
        )}
      </div>
    </header>
  )
}

export default Hero
